import numpy as np

def simulate_sequence():
    # Initialize variables
    sequence = []
    remaining_numbers = {1, 2, 3, 4}

    while len(remaining_numbers) > 0:
        roll = np.random.choice([1, 2, 3, 4])

        if not sequence or (sequence[-1] + roll != 5):
            sequence.append(roll)
            remaining_numbers.discard(roll)

    return len(sequence)

def calculate_expected_length(num_simulations=10000):
    total_length = 0

    for _ in range(num_simulations):
        total_length += simulate_sequence

