def count_rainbow_numbers():
    count = 0
    # Iterate through all four-digit numbers
    for num in range(1000, 10000):
        str_num = str(num)
        a, b, c, d = int(str_num[0]), int(str_num[1]), int(str_num[2]), int(str_num[3])

        # Check the condition for Rainbow Four-Digit Numbers
