def min_initial_soda_purchase(total_people):
    # Initialize variables
    current_bottles = 0
    additional_bottles = 0

    while total_people > current_bottles:
        # Calculate remaining people without soda
        remaining_people = total_people - current_bottles

        # Calculate additional bottles needed based on exchange rate
        if remaining_people >= 5:
            additional_bottles += remaining_people // 5
            current_bottles += remaining_people // 5 + remaining_people % 5
        else:
            additional_bottles += remaining_people
            break

    return additional_bottles

